mapper.select:
SELECT *
FROM blog
WHERE 1 = 1

    AND userName = #{blog.userName}


LIMIT ${pageParam.start}, ${pageParam.size}

mapper.update:
UPDATE
blog
SET
likes = likes + #{likes}
WHERE id = #{blogId}

mapper.select:
SELECT *
FROM blog
WHERE userName IN (
SELECT followee FROM follow
WHERE follower = #{userName}
)
ORDER BY time LIMIT ${start}, ${pageSize}

SELECT likes
FROM blog
WHERE id = #{blogId}

SELECT userName
FROM blog
WHERE id = #{blogId}
